import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import styled from 'styled-components';
import logo from '../brand.png';

export default class Navbar extends Component {
    render() {
        return(

            //Navbar
            <nav className="navbar navbar-expand-sm navbar-dark bg-light px-sm-5">
                <Link to='/'>
                    <img src={logo} alt="Adidas" className="navbar-brand" />
                </Link>
                
                
                <ul className="navbar-nav align-items-center">
                    <li className="nav-item ml-5"><Link to="/" className="nav-link text-dark">Produtos</Link></li>
                    <li className="nav-item"><Link to="/" className="nav-link text-dark">Sobre</Link></li>
                    <li className="nav-item"><Link to="/" className="nav-link text-dark">Contato</Link></li>
                    <li className="nav-item"><Link to="/" className="nav-link text-dark">Cadastro</Link></li>
                </ul>

                <Link to='/cart' className="ml-auto">
                    <ButtonContainer>
                        <span><i class="fas fa-shopping-cart" /></span>
                    </ButtonContainer>
                </Link>
            </nav>
        ); 
    }
}

const ButtonContainer = styled.button `
    font-size: 16px;
    text-transform: capitalize;
    color: var(--mainDarker);
    background: transparent;
    border: none;
    opacity: .4;
    transition: all 0.5 ease-in-out;
        &:hover {
        opacity: 1;
        }

        &:focus {
            outline: 0;
        }
`
