import React, { Component } from 'react';
import styled from 'styled-components';


export default class Footer extends Component {
    render() {
        return (
            <FooterWrapper className="container-fluid bg-purple">
                <div className="container">
                <div className="row">
                   <div className="col-sm">
                       <ul className="mt-2">
                           <li><img src="Images/facebook.png" className="social-icon" title="Facebook" alt="Facebook" /></li>
                           <li><img src="Images/twitter.png" className="social-icon" title="Twitter" alt="Twitter" /></li>
                           <li><img src="Images/pinterest.png" className="social-icon" title="Pinterest" alt="Pinterest" /></li>
                           <li><img src="Images/googlePlus.png" className="social-icon" title="Google +" alt="Google +" /></li>
                       </ul>

                       <p className="copy">2019 Merlin's Potions. All Rights Reserved</p>
                   </div>
                   <div className="col-sm">
                   <p className="text-sig-newsletter">Sign up for our Newsletter</p>
                        <p className="text-descount-newsletter">Sign up for our Newsletter & get exclusive descount</p>
  
                    <form className="form-inline my-2 my-lg-0">
                        <input className="form-control mr-sm-2" type="search" placeholder="Entter your email" aria-label="Enter your email" />
                        <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Submit</button>
                    </form>
                   </div>
                   </div>
                </div>

            </FooterWrapper>
            
        )
    }
}

const FooterWrapper = styled.footer `
background: var(--mainPurple);
height: 130px!important;

 ul {
    display: flex;
    list-style: none;
}

ul li {
    margin: 5px;
    cursor: pointer;
}

.social-icon img {
    width: 30px!important;
}

.copy {
    font-size: 14px;
    color: var(--mainSnow);
}

.text-sig-newsletter {
    color: var(--mainSnow);
}

.text-descount-newsletter {
    color: var(--mainSnow);
}

`
