import styled from 'styled-components';

export const ButtonContainer = styled.button `
    font-size: 16px;
    text-transform: capitalize;
    color: var(--mainDark);
    background: transparent;
    border: none;
    transition: all 0.5 ease-in-out;
        &:hover {
        font-size: 18px;
        opacity: 1;
        }

        &:focus {
            outline: none;
        }
`
