import React, { Component } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { ProductConsumer} from './context';

export default class Product extends Component {
    render() {
        const { id, title, image, price, inCart } = this.props.product;

        return (
            //Grid de Produtos
            <ProductWrapper className="col-xs-3 col-sm-6 col-md-6 col-lg-4 mx-auto my-5">
                <div class="card">
                    <div class="img-container p-5" onClick={() => console.log('Você clicou na imagem do produto')}>
                        <Link to='/details'>
                            <img src={image} alt="Imagem do produto" className="card-img-top imageProduct" />
                        </Link>
                        <button className="cart-btn" disabled={inCart ? true : false} onClick={()=>{console.log('Adicionado ao carrinho');}}>
                        {inCart?(<p className="text-capitalize mb-0" disabled>inCart</p>):(<a>Comprar</a>)}
                        </button>
                    </div>

                    <div className="card-footer d-flex-justify-content-between">
                        <p className="align-self-center mb-0">
                            {title}
                        </p>

                        <h5 className="font-italic mb-0">
                            <span className="mr-1">R$</span>
                            {price}
                        </h5>
                    </div>
                </div>
            </ProductWrapper>
            
        );
    }
}

const ProductWrapper = styled.div `
    .card {
        border-color: 1px solid #DDD;
        transiton: all 1s linear;
        height: 320px!important;
    }

    .card-footer {
        background: transparent;
        transition: all 1s linear;
    }
    &:hover {
        .card {
            box-shadow: 2px 2px 5px 0px rgba(0, 0, 0, 0.2);
        }
    }

    .img-container {
        position: relative;
        overflow: hidden;
    }

    .card-img-top {
        transition: all 1s;
    }

    .imageProduct {
        position: relative;
        top: 50%!important;
        left: 50%!important;
        transform: translate(-50%, -50%)!important;
        width: 200px!important;
    }

    .imgage:hover .card-img-top {
        animation: shake 0.5s infinite; 
    }

    .cart-btn {
        position: absolute;
        bottom: 0px;
        right: 0px;
        padding: 0.2rem 0.4rem;
        background: var(--mainPurple);
        border: none;
        border-radius: 15px 0px 0px 0px;
        font-size: 18px;
        color: var(--mainDarker);
        transform: translate(100%, 100%);
        transition: all 1s;
    }

    .img-container:hover .cart-btn {
        transform: translate(0, 0);
    }

    @keyframes shake {
        0% { transform: translate(1px, 1px) rotate(0deg); }
        10% { transform: translate(-1px, -2px) rotate(-1deg); }
        20% { transform: translate(-3px, 0px) rotate(1deg); }
        30% { transform: translate(3px, 2px) rotate(0deg); }
        40% { transform: translate(1px, -1px) rotate(1deg); }
        50% { transform: translate(-1px, 2px) rotate(-1deg); }
        60% { transform: translate(-3px, 1px) rotate(0deg); }
        70% { transform: translate(3px, 1px) rotate(-1deg); }
        80% { transform: translate(-1px, -1px) rotate(1deg); }
        90% { transform: translate(1px, 2px) rotate(0deg); }
        100% { transform: translate(1px, -2px) rotate(-1deg); }
      }

    @media only screen and (max-width: 1024px) {
        ..card-img-top img {
            width: 50px;
        }
    }
`
