import React, { Component } from 'react';
import styled from 'styled-components';


export default class Footer extends Component {
    render() {
        return (
            <FooterWrapper className="container-fluid bg-purple">
                <div class="container">
                <div class="row">
                   <div class="col-sm">
                       <ul className="my-1">
                           <li><img src="Images/facebook.png" class="social-icon" /></li>
                           <li><img src="Images/twitter.png" class="social-icon" /></li>
                           <li><img src="Images/pinterest.png" class="social-icon" /></li>
                           <li><img src="Images/googlePlus.png" class="social-icon" /></li>
                       </ul>

                       <p id="copy">2019 Merlin's Potions. All Rights Reserved</p>
                   </div>
                   <div class="col-sm">B</div>
                   </div>
                </div>

            </FooterWrapper>
            
        )
    }
}

const FooterWrapper = styled.footer `
background: var(--mainPurple);

 ul {
    display: flex;
    list-style: none;
}

ul li {
    margin: 5px;
    cursor: pointer;
}

.social-icon img {
    width: 30px!important;
}

#copy {
    font-size: 14px;
    color: var(--mainSnow);
}

`
