import React, { Component } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { ProductConsumer} from './context';

export default class Product extends Component {
    render() {
        const { id, title, image, price, inCart } = this.props.product;

        return (
            <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
                <div class="card">
                    <div class="img-container p-5" onClick={() => console.log('Você clicou na imagem do produto')}>
                        <Link to='/details'>
                            <img src={image} alt="Imagem do produto" className="card-img-top" />
                        </Link>
                        <button className="cart-btn" disabled={inCart ? true : false} onClick={()=>{console.log('Adicionado ao carrinho');}}>
                        {inCart?(<p className="text-capitalize mb-0" disabled>inCart</p>):(<i className="fas fa-cart-plus" />)}
                        </button>
                    </div>

                    <div className="card-footer d-flex-justify-content-between">
                        <p className="align-self-center mb-0">
                            {title}
                        </p>

                        <h5 className="font-italic mb-0">
                            <span className="mr-1">R$</span>
                            {price}
                        </h5>
                    </div>
                </div>
            </ProductWrapper>
            
        );
    }
}

const ProductWrapper = styled.div `

